import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import React, {useState} from 'react';

export default function App() {
  const [portifolio] = useState("Portifólio");
  const [btn, setBtn] = useState("Sair");

  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.sair}>
      <Text style={styles.textButtonSair}>{btn}</Text>
      <Text style={styles.text}>{portifolio}</Text>
      </TouchableOpacity>
    </View>

  );
}

const styles = StyleSheet.create({
  container:{
    // backgroundColor: "#f1f",
    marginTop: 28,
    color: "#fff",
    alignContent: ""
  },
  sair: {
    color: "#fff",
    backgroundColor: "#abce99"
  },
  textButtonSair:{
    color: "#fff",
    textAlign: "right",
    marginRight: 15,
    // textAlign: "justify"
  }
})
